#!/usr/bin/perl

use Data::Dumper;

#Parsing beacon positions

$cw = 7;

$r->{0} = "0:0,1:0,2:0,3:0";
$r->{1} = "1:0,0:1,1:1,2:1,1:2";
$r->{2} = "0:0,1:0,2:0,2:1,2:2";
$r->{3} = "0:0,0:1,0:2,0:3";
$r->{4} = "0:0,0:1,1:0,1:1";

sub jet {
    my ($rock, $cmd) + @_;
    if ($cmd eq '<')
    {

    }
}

sub a {
    my ($rock) = @_;

}

sub bake {
    my ($rock, $x, $y) = @_;
    for my $a (split ',', $rock)
    {
        ($ax,$ay) = split ':', $a;
        $cave->{$ay+$y}->{$ax+$x} = '#';
    }
}

sub freespace {
    my ($rock, $x, $y) = @_;
    for my $a (split ',', $rock)
    {
        ($ax,$ay) = split ':', $a;
        if (defined $cave->{$ay+$y-1})
        {
            ($cave->{$ay+$y-1}->{$ax+$x} eq '#') && return 0;
        }
    }
    return 1;
}

sub freeright {
    my ($rock, $x, $y) = @_;
    for my $a (split ',', $rock)
    {
        ($ax,$ay) = split ':', $a;
        return 0 unless $ax+$x+1 < $cw;

        if (defined $cave->{$ay+$y})
        {
            ($cave->{$ay+$y}->{$ax+$x+1} eq '#') && return 0;
        }
    }
    return 1;    
}

sub freeleft {
    my ($rock, $x, $y) = @_;
    for my $a (split ',', $rock)
    {
        ($ax,$ay) = split ':', $a;
        return 0 unless $ax+$x-1 >= 0;
        if (defined $cave->{$ay+$y})
        {
            ($cave->{$ay+$y}->{$ax+$x-1} eq '#') && return 0;
        }
    }
    return 1;    
}

sub trimcave {
    $poly = {};
    $top = (reverse sort {$a <=> $b} keys $cave)[0];
    for $i (0..$cw-1)
    {
        if (not defined $cave->{$top}->{$i})
        {
            $poly->{$top}->{$i} = 1;
        }
    }

    while (1)
    {
        $added = 0;
        for my $y (keys $poly) {
            for my $x (keys $poly->{$y}) {
                if ($y>0 && (not defined $poly->{$y-1}->{$x}) && (not defined $cave->{$y-1}->{$x}))
                {
                    $poly->{$y-1}->{$x} = $added++;
                }
                if ($x>0 && (not defined $poly->{$y}->{$x-1}) && (not defined $cave->{$y}->{$x-1}))
                {
                    $poly->{$y}->{$x-1} = $added++;
                }
                if ($x<($cw-1) && (not defined $poly->{$y}->{$x+1}) && (not defined $cave->{$y}->{$x+1}))
                {
                    $poly->{$y}->{$x+1} = $added++;
                }
            }
        }
        if ($added == 0)
        {
            return (sort {$a <=> $b} keys $poly)[0];
        }
    }
}

sub printcave {
    for $yy (0..10000) {
        $y = 10000 - $yy;
        if (exists $cave->{$y})
        {
            for $x (0..$cw-1) {
                print $cave->{$y}->{$x} || '.';
            }
            print " - " . $y;
            print "\n";
        }
    }
    print "\n";
}

